$(document).ready(function(){$(".carousel__inner").slick({speed:1200,prevArrow:'<button type="button" class="slick-prev"><img src="assets/img/icons/chevron-left.png" alt="chevron"></button>',nextArrow:'<button type="button" class="slick-next"><img src="assets/img/icons/chevron-right.png" alt="chevron"></button>',responsive:[{breakpoint:992,settings:{dots:!0,arrows:!1}}]})}),window.addEventListener("DOMContentLoaded",()=>{const t=document.querySelector(".menu"),e=document.querySelectorAll(".menu_item"),n=document.querySelector(".hamburger");n.addEventListener("click",()=>{n.classList.toggle("hamburger_active"),t.classList.toggle("menu_active")}),e.forEach(e=>{e.addEventListener("click",()=>{n.classList.toggle("hamburger_active"),t.classList.toggle("menu_active")})})});